package com.blockstream.test;

import com.blockstream.libwally.Wally;

public class test_elements_tx {

    public test_elements_tx() { }

    public static void assert_eq(final Object expected, final Object actual, final String message) {
        if(!expected.equals(actual)) {
            System.out.println(expected);
            System.out.println(actual);
            throw new RuntimeException(message);
        }
    }

    private String h(final byte[] bytes) { return Wally.hex_from_bytes(bytes); }
    private byte[] h(final String hex) { return Wally.hex_to_bytes(hex); }

    public void test_elements_taproot_sighash() {
        byte[] bytes_out = new byte[32];
        final String tx_hex = "";
        Object tx = Wally.tx_from_hex(tx_hex, Wally.WALLY_TX_FLAG_USE_ELEMENTS);

        final byte[] genesis_hex = h("00902a6b70c2ca83b5d9c815d96a0e2f4202179316970d14ea1847dae5b1ca21");
        byte[] genesis = new byte[32];
        for(int i = 0; i < genesis.length; i++) {
            genesis[i] = genesis_hex[genesis_hex.length - i - 1];
        }

        final Object scripts = Wally.map_init(2);
        final Object values = Wally.map_init(2);
        final Object assets = Wally.map_init(2);
        final Object cache = Wally.map_init(16); // TODO: Add a wally constant for this

        Wally.map_add_integer(scripts, 0, h("5120900d1d75269396d4220c4529527dbcb746a6093c7209cea2d76a87c8ab9447fc"));
        Wally.map_add_integer(scripts, 1, h("5120b850370392dafc3e7d02db13b447c927e98b6439681fea582401e570904cd6a5"));
        Wally.map_add_integer(values, 0, h("083696f3aefdf18c044cab5ef189c575d79a096db1c6bc17f152cdf1fd3cb136f3"));
        Wally.map_add_integer(values, 1, h("097dfabf3388968a388633a058784a5bff414a53801cf32109be34b92123d3096e"));
        Wally.map_add_integer(assets, 0, h("0aacb400cb406593951f7f7e61b63f10db73ee63b861c31fd266cce84f145f87f2"));
        Wally.map_add_integer(assets, 1, h("0ae983aaefe54f21571238e95a93a8c965ed737bc217ea480485be1e1969adc279"));

        final int input_idx = 0, key_version = 0;
        final int sighash = Wally.WALLY_SIGHASH_DEFAULT, sighash_type = Wally.WALLY_SIGTYPE_SW_V1;
        final long codesep_pos = 0xFFFFFFFFL;
        final byte[] tapleaf_script = null, annex = null;

        final String expected = "2c478ce6d5637e0ea8be37a53090e0955b6c501773fccf6738520cfcc5442150";

        Wally.tx_get_input_signature_hash(tx, input_idx, scripts, assets, values,
            tapleaf_script, key_version, codesep_pos, annex, genesis, sighash, sighash_type,
            cache, bytes_out
        );

        assert_eq(expected, h(bytes_out), "different sighash");

        Wally.map_free(cache);
        Wally.map_free(assets);
        Wally.map_free(values);
        Wally.map_free(scripts);
    }

    public static void main(final String[] args) {
        final test_elements_tx t = new test_elements_tx();
        t.test_elements_taproot_sighash();
    }
}
